examples.code:
var arr = [ "a", "b", "c", "d", "e" ];
$("div").text(arr.join(", "));

arr = jQuery.map(arr, function(n, i){
  return (n.toUpperCase() + i);
});
$("p").text(arr.join(", "));

arr = jQuery.map(arr, function (a) {
  return a + a;
});
$("span").text(arr.join(", "));

examples.css:
div { color:blue; }
p { color:green; margin:0; }
span { color:red; }

