examples.css:
div.out {
width:40%;
height:120px;
margin:0 15px;
background-color:#D6EDFC;
float:left;
}
div.in {
width:60%;
height:60%;
background-color:#FFCC00;
margin:10px auto;
}
p {
line-height:1em;
margin:0;
padding:0;
}

examples.code:
var i = 0;
$("div.overout").mouseover(function(){
  $("p:first",this).text("mouse over");
}).mouseout(function(){
  $("p:first",this).text("mouse out");
  $("p:last",this).text(++i);
});

var n = 0;
$("div.enterleave").mouseenter(function(){
  $("p:first",this).text("mouse enter");
}).mouseleave(function(){
  $("p:first",this).text("mouse leave");
  $("p:last",this).text(++n);
});
